DB_NAME = 'lila_game.db'

def init_db():
    conn = sqlite3.connect(DB_NAME)
    cur = conn.cursor()
    cur.execute('''
        CREATE TABLE IF NOT EXISTS users (
            user_id INTEGER PRIMARY KEY,
            position INTEGER DEFAULT 68,
            entered BOOLEAN DEFAULT 0,
            game_active BOOLEAN DEFAULT 0,
            pending_sixes INTEGER DEFAULT 0,
            waiting_for_roll BOOLEAN DEFAULT 0,
            query TEXT DEFAULT '',
            history TEXT DEFAULT ''
        )
    ''')
    conn.commit()
    conn.close()

init_db()

def get_user(user_id):
    conn = sqlite3.connect(DB_NAME)
    cur = conn.cursor()
    cur.execute('SELECT * FROM users WHERE user_id = ?', (user_id,))
    row = cur.fetchone()
    conn.close()
    if row:
        return {
            'user_id': row[0],
            'position': row[1],
            'entered': bool(row[2]),
            'game_active': bool(row[3]),
            'pending_sixes': row[4],
            'waiting_for_roll': bool(row[5]),
            'query': row[6] or '',
            'history': row[7] or ''
        }
    return None

def save_user(data):
    conn = sqlite3.connect(DB_NAME)
    cur = conn.cursor()
    cur.execute('''
        INSERT OR REPLACE INTO users (user_id, position, entered, game_active, pending_sixes, waiting_for_roll, query, history)
        VALUES (?, ?, ?, ?, ?, ?, ?, ?)
    ''', (
        data['user_id'],
        data['position'],
        int(data['entered']),
        int(data['game_active']),
        data['pending_sixes'],
        int(data['waiting_for_roll']),
        data['query'],
        data['history']
    ))
    conn.commit()
    conn.close()

def add_to_history(user_id, entry):
    user = get_user(user_id)
    if user:
        new_history = user['history'] + entry + '\n'
        lines = new_history.strip().split('\n')[-20:]
        user['history'] = '\n'.join(lines) + ('\n' if lines else '')
        save_user(user)

def reset_game(user_id):
    data = {
        'user_id': user_id,
        'position': 68,
        'entered': False,
        'game_active': True,
        'pending_sixes': 0,
        'waiting_for_roll': False,
        'query': '',
        'history': ''
    }
    save_user(data)
    return data

