    if dice_value == 6:
        user['pending_sixes'] += 1
        user['waiting_for_roll'] = True
        save_user(user)
        
        if user['pending_sixes'] == 4:
            user['pending_sixes'] = 0
            user['position'] = 1
            user['entered'] = True
            save_user(user)
            add_to_history(user_id, "‚ö†Ô∏è –ß–µ—Ç—ã—Ä–µ —à–µ—Å—Ç—ë—Ä–∫–∏ –ø–æ–¥—Ä—è–¥ ‚Äî –≤–æ–∑–≤—Ä–∞—Ç –∫ –†–æ–∂–¥–µ–Ω–∏—é")
